import React from 'react';
import Test from './test'
import { Button } from '@material-ui/core'
import { TextField } from '@material-ui/core'

export default class App extends React.Component {
    constructor() {
        super()
        this.state = ({
            toDisplay: false,
            num: 0
        })
    }

    change = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    submit = () => {
        if(this.state.num > 0) {
            console.log('in submit')
            this.setState({
                toDisplay: true
            })
        } 
    }

    render() {
        return (
            <div className="App" style={{ paddingTop: '200px', width: '100%', textAlign: 'center'}}>
                {!this.state.toDisplay &&
                <>
                    {/* <TextField
                    label
                    /> */}
                    <TextField
                    label="Number of words"
                    name="num"
                    variant="filled"
                    type="number"
                    onChange={this.change}
                    value={this.state.num}
                    required
                    />
                    <br />
                    <Button type="submit" variant="contained"  style={{  margin: '10px'}}>Start</Button>
                </>
                }
                {this.state.toDisplay &&
                <Test changeDisplay={this.setState({toDisplay: false})} num={this.state.num}/>
                }
            </div>
        );
    }
}
