import React from 'react';
import logo from './logo.svg';
import './App.css';
import { TextField } from '@material-ui/core'

export default class App extends React.Component {
  constructor() {
    super();
    this.state = ({
      word: ['w', 'o', 'r', 'd'],
      correct: [],
      text: ''
    });
  }

  componentDidMount = () => {
    var c = this.state.correct
    for(var i = 0; i < this.state.word.length; i++) {
      c.push("black")
    }
    this.setState({
      correct: c
    });
  }

  change = (e) => {
    this.setState({
      text: e.target.value
    })
    var pos = this.state.text.length-1
    var key = this.state.text[pos];
    var c = this.state.correct;
    if(key == this.state.word[pos]) {
      c[pos] = "green";
    } else {
      c[pos] = "red"
    }
  }


  render() {
    var correct;

    var checker = this.state.word.map((element, index) => {
      return(
      <p
      id={`let${index + 1}`}
      style={{color: this.state.correct[index]}}>
        {element}
      </p>
      );
    })
    return (
      <div className="App" style={{ height:'1000px', width: '100%', backgroundColor: 'gray'}}>

        <div style={{margin: 'auto', marginBottom: '50vh', paddingTop: '300px'}}>
          {checker}
          <TextField 
            style={{ color: 'white' }} 
            id="filled-basic" 
            label="text" 
            multiline
            onChange={this.change}
            rows={4}
            variant="filled"
            value={this.state.text}
            />

        </div>
      </div>
    );
  }
}
